import { Col, Row } from "react-bootstrap";
import CustomButton from "./CustomButton";

const ItemGroup = ({ heading, items, additionalLinks }) => {
    return (
        <Col>
            <h5 className="text-success">{heading}</h5>
            {items.map(item => (
                <Row key={item.label}>
                    <Col>{item.label}</Col>
                    {item.chevron && <Col xs={1}>&#8250;</Col>}
                </Row>
            ))}

            {additionalLinks.map(link => (
                <Row key={link} className="mt-1">
                    <h6 className="text-success">{link}</h6>
                </Row>
            ))}
        </Col>
    )
}

const Clothing = () => {
    return (
        <Row
            className="px-2"
            style={{
                width: "35rem",
                display: "flex",
                justifyContent: "center"
            }}>
            <Row className="pb-2">
                <Col xs={6} className="px-3 py-2 mt-2 border-end">
                    <ItemGroup
                        heading="Accessories"
                        items={[
                            {
                                label: "Benies",
                                chevron: true,
                            },
                            {
                                label: "Caps and Hats",
                                chevron: true,
                            },
                            {
                                label: "Glasses",
                            },
                            {
                                label: "Gloves",
                            },
                            {
                                label: "Jewellery",
                            },
                            {
                                label: "Scarves",
                            },
                        ]}
                        additionalLinks={["All Accessories"]} />
                </Col>
                <Col xs={6} className="px-3 py-2 mt-2">
                    <ItemGroup
                        heading="Bottoms"
                        items={[
                            {
                                label: "Casual Trousers",
                            },
                            {
                                label: "Jeans",
                                chevron: true,
                            },
                            {
                                label: "Leggings",
                            },
                            {
                                label: "Shorts",
                            },
                        ]}
                        additionalLinks={["All Bottoms"]} />
                </Col>
            </Row>
            <Row className="pt-1 border-top">
                <Col xs={6} className="px-3 py-2 mt-2 border-end">
                    <ItemGroup
                        heading="Jackets"
                        items={[
                            {
                                label: "Blazors",
                            },
                            {
                                label: "Bomber Jackets",
                            },
                            {
                                label: "Denim Jackets",
                            },
                            {
                                label: "Duffle Coats",
                            },
                            {
                                label: "Leather Jackets",
                            },
                            {
                                label: "Parkas",
                            },
                        ]}
                        additionalLinks={["All Jackets"]} />
                </Col>
                <Col xs={6} className="px-3 py-2 mt-2">
                    <ItemGroup
                        heading="Tops"
                        items={[
                            {
                                label: "Cardigans",
                            },
                            {
                                label: "Coats",
                            },
                            {
                                label: "Polo Shirts",
                            },
                            {
                                label: "Shirts",
                            },
                            {
                                label: "T-Shirts",
                                chevron: true,
                            },
                            {
                                label: "Vests",
                            },
                        ]}
                        additionalLinks={["All Tops"]} />
                </Col>
            </Row>
            <CustomButton>All Clothing</CustomButton>
        </Row>
    );
}

export default Clothing;
