import { Row, Col, Image } from "react-bootstrap";
import ServiceIcon from "../assets/servicesIcon.png";
import CustomButton from "./CustomButton";

const ServiceItem = ({ label, image, description }) => {
    return (
        <Col xs={6} className="my-2 hover">
            <Row className="align-items-center">
                <Col xs={2}>
                    <Image
                        style={{
                            width: "100%"
                        }}
                        src={image} />
                </Col>
                <Col>
                    <h6 className="text-success">{label}</h6>
                    <p>{description}</p>
                </Col>
            </Row>
        </Col>
    );
}

const serviceItems = [
    {
        label: "Service #1",
        image: ServiceIcon,
        description: "This is the item description",
    },
    {
        label: "Service #2",
        image: ServiceIcon,
        description: "This is the item description",
    },
    {
        label: "Service #3",
        image: ServiceIcon,
        description: "This is the item description",
    },
    {
        label: "Service #4",
        image: ServiceIcon,
        description: "This is the item description",
    },
    {
        label: "Service #5",
        image: ServiceIcon,
        description: "This is the item description",
    },
    {
        label: "Service #6",
        image: ServiceIcon,
        description: "This is the item description",
    },
    {
        label: "Service #7",
        image: ServiceIcon,
        description: "This is the item description",
    },
    {
        label: "Service #8",
        image: ServiceIcon,
        description: "This is the item description",
    },
];

const Services = () => {
    return (
        <Row
            className="px-2"
            style={{
                width: "40rem"
            }}>
            {serviceItems.map(item => (
                <ServiceItem
                    key={item.label}
                    {...item} />
            ))}
            <CustomButton>Browse Services</CustomButton>
        </Row>
    );
}

export default Services;
